                               address_line1, city, state,
                               post_zip_code, created_by, created_date,
                               state_code, county
                              )
                       VALUES (v_addressid, in_countryid, in_buildingno,
                               in_addressline1, in_city, in_state,
                               in_postzipcode, in_modifiedby, SYSDATE (),
                               in_state_code, in_county
                              );

                  -- ##### Change for 904 - B-End validation  begin
                  UPDATE site
                     SET customer_report_site_name =
                                                    in_customer_reportsitename,
                         local_business_customer_name =
                                                   in_local_business_cust_name,
                         address_id = v_addressid,
                         classic_site_id = NULL,
                         billing_id = NULL,
                         telephone_area_code = in_telephoneareacode,
                         line_of_site_billing = in_lineofsitebilling,
                         notes = NULL,
                         modified_by = in_modifiedby,
                         modified_date = SYSDATE ()
                   WHERE site_id = in_siteid AND customer_id = in_customerid;

                                -- ##### Change for 904 - B-End validation  end
                  IF (in_additionalsite IS NOT NULL)
                  THEN
                     BEGIN
                        v_row := in_additionalsite;

                        SELECT pkg_common.fn_countfields (v_row, '|')
                          INTO v_fldcnt
                          FROM DUAL;

                        IF (v_fldcnt = 8)
                        THEN
                           BEGIN
                              v_floor :=
                                 SUBSTR (v_row,
                                         1,
                                         INSTR (v_row, '|', 1, 1) - 1
                                        );
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_room :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_addrline2 :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_classicsiteid :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_billingid :=
                                 TO_NUMBER (SUBSTR (v_row,
                                                    1,
                                                      INSTR (v_row, '|', 1, 1)
                                                    - 1
                                                   )
                                           );
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              /* Sprint2 Clean order managment SQE904 Starts */
                              v_notes :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_ppsr_notes :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_cntry_spec_notes_req :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_url := v_row;

                              /* Sprint2 Clean order managment SQE904  end*/
                              UPDATE address
                                 SET FLOOR = v_floor,
                                     room = v_room,
                                     address_line2 = v_addrline2
                               WHERE address_id = v_addressid;

                              UPDATE site
                                 SET classic_site_id = v_classicsiteid,
                                     billing_id = v_billingid,
                                     notes = v_notes,
                                     ppsr_note = v_ppsr_notes,
                                     cntry_spec_notes_req =
                                                        v_cntry_spec_notes_req,
                                     url = v_url
                               WHERE site_id = in_siteid
                                 AND customer_id = in_customerid;
                           END;
                        ELSE
                           BEGIN
                              out_status := pkg_constants.con_status_failure; -- Added as part of exception handling
                              out_errmsg := 'Invalid Additional Site details';
                           END;
                        END IF;
                     END;
                  END IF;                              --end in_additionalsite

                  v_row := NULL;

                  IF (in_pricontact IS NOT NULL)
                  THEN
                     BEGIN
                        v_row := in_pricontact;

                        SELECT pkg_common.fn_countfields (v_row, '|')
                          INTO v_fldcnt
                          FROM DUAL;

                        IF (v_fldcnt = 8)
                        THEN
                           BEGIN
                              v_prifirstname :=
                                 SUBSTR (v_row,
                                         1,
                                         INSTR (v_row, '|', 1, 1) - 1
                                        );
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_prilastname :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_prijobtitle :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_priemail :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_priphone :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_primobile :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              --v_prifax := v_row;
/********/
                              /*HVPN Design change R3 (2010) */
                              /*adding PSTN number and Notes in the primary contact section  */
                              v_prifax :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_pstn_number :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_primary_notes := v_row;

                              /*R3 ends */
                              /*******/
                              INSERT INTO contact
                                          (contact_id,
                                           first_name, last_name,
                                           phone_number, mobile_number, fax,
                                           contact_type,
                                           job_title, email,
                                           created_by, created_date,
                                           pstn_number, primary_notes
                                          )
                                   VALUES (contact_id.NEXTVAL,
                                           v_prifirstname, v_prilastname,
                                           v_priphone, v_primobile, v_prifax,
                                           pkg_constants.con_primary_sitecontact,
                                           v_prijobtitle, v_priemail,
                                           in_modifiedby, SYSDATE (),
                                           v_pstn_number, v_primary_notes
                                          )
                                RETURNING contact_id
                                     INTO v_pricontactid;

                              INSERT INTO site_contact_detail
                                          (site_contact_detail_id,
                                           site_id, contact_id,
                                           created_by, created_date
                                          )
                                   VALUES (site_contact_detail_id.NEXTVAL,
                                           in_siteid, v_pricontactid,
                                           in_modifiedby, SYSDATE
                                          );
                           END;
                        ELSE
                           BEGIN
                              out_status := pkg_constants.con_status_failure; -- Added as part of exception handling
                              out_errmsg := 'Invalid Primary Contact details';
                           END;
                        END IF;                                --end of fldcnt
                     END;
                  END IF;                    --end of primary contact not null

                  v_row := NULL;

                  IF (in_seccontact IS NOT NULL)
                  THEN
                     BEGIN
                        v_row := in_seccontact;

                        SELECT pkg_common.fn_countfields (v_row, '|')
                          INTO v_fldcnt
                          FROM DUAL;

                        IF (v_fldcnt = 6)
                        THEN
                           BEGIN
                              v_secfirstname :=
                                 SUBSTR (v_row,
                                         1,
                                         INSTR (v_row, '|', 1, 1) - 1
                                        );
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_seclastname :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_secjobtitle :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_secemail :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_secphone :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_secmobile :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_secfax := v_row;

                              INSERT INTO contact
                                          (contact_id,
                                           first_name, last_name,
                                           phone_number, mobile_number, fax,
                                           contact_type,
                                           job_title, email,
                                           created_by, created_date
                                          )
                                   VALUES (contact_id.NEXTVAL,
                                           v_secfirstname, v_seclastname,
                                           v_secphone, v_secmobile, v_secfax,
                                           pkg_constants.con_secondary_sitecontact,
                                           v_secjobtitle, v_secemail,
                                           in_modifiedby, SYSDATE ()
                                          )
                                RETURNING contact_id
                                     INTO v_seccontactid;

                              INSERT INTO site_contact_detail
                                          (site_contact_detail_id,
                                           site_id, contact_id,
                                           created_by, created_date
                                          )
                                   VALUES (site_contact_detail_id.NEXTVAL,
                                           in_siteid, v_seccontactid,
                                           in_modifiedby, SYSDATE
                                          );
                           END;
                        ELSE
                           BEGIN
                              out_status := pkg_constants.con_status_failure; -- Added as part of exception handling
                              out_errmsg :=
                                          'Invalid Secondary Contact details';
                           END;
                        END IF;                                --end of fldcnt
                     END;
                  END IF;                  --end of secondary contact not null

                  v_row := NULL;

                  IF (in_techcontact IS NOT NULL)
                  THEN
                     BEGIN
                        v_row := in_techcontact;

                        SELECT pkg_common.fn_countfields (v_row, '|')
                          INTO v_fldcnt
                          FROM DUAL;

                        IF (v_fldcnt = 6)
                        THEN
                           BEGIN
                              v_techfirstname :=
                                 SUBSTR (v_row,
                                         1,
                                         INSTR (v_row, '|', 1, 1) - 1
                                        );
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_techlastname :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_techjobtitle :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
                                           LENGTH (v_row)
                                         - INSTR (v_row, '|', 1, 1)
                                        );
                              v_techemail :=
                                 SUBSTR (v_row, 1,
                                         INSTR (v_row, '|', 1, 1) - 1);
                              v_row :=
                                 SUBSTR (v_row,
                                         INSTR (v_row, '|', 1, 1) + 1,
