                                                             (pkg_constants.con_primary_sitecontact
                                                             )
                                                   AND s.site_id = v_siteid
                                                   AND customer_id =
                                                                 in_customerid;

                                                IF (v_contactcnt <> 0)
                                                THEN
                                                   BEGIN
                                                      SELECT sc.contact_id
                                                        INTO v_pricontactid
                                                        FROM site s,
                                                             site_contact_detail sc,
                                                             contact c
                                                       WHERE s.site_id =
                                                                    sc.site_id
                                                         AND sc.contact_id =
                                                                  c.contact_id
                                                         AND UPPER
                                                                (c.contact_type
                                                                ) =
                                                                UPPER
                                                                   (pkg_constants.con_primary_sitecontact
                                                                   )
                                                         AND s.site_id =
                                                                      v_siteid
                                                         AND customer_id =
                                                                 in_customerid;
                                                   END;
                                                ELSE
                                                   BEGIN
                                                      v_pricontactid := NULL;
                                                   END;
                                                END IF;

                                                --update secondary contact details added on 30/12/2009
                                                SELECT COUNT (*)
                                                  INTO v_contactcnt
                                                  FROM site s,
                                                       site_contact_detail sc,
                                                       contact c
                                                 WHERE s.site_id = sc.site_id
                                                   AND sc.contact_id =
                                                                  c.contact_id
                                                   AND UPPER (c.contact_type) =
                                                          UPPER
                                                             (pkg_constants.con_secondary_sitecontact
                                                             )
                                                   AND s.site_id = v_siteid
                                                   AND customer_id =
                                                                 in_customerid;

                                                IF (v_contactcnt <> 0)
                                                THEN
                                                   BEGIN
                                                      SELECT sc.contact_id
                                                        INTO v_seccontactid
                                                        FROM site s,
                                                             site_contact_detail sc,
                                                             contact c
                                                       WHERE s.site_id =
                                                                    sc.site_id
                                                         AND sc.contact_id =
                                                                  c.contact_id
                                                         AND UPPER
                                                                (c.contact_type
                                                                ) =
                                                                UPPER
                                                                   (pkg_constants.con_secondary_sitecontact
                                                                   )
                                                         AND s.site_id =
                                                                      v_siteid
                                                         AND customer_id =
                                                                 in_customerid;
                                                   END;
                                                ELSE
                                                   BEGIN
                                                      v_seccontactid := NULL;
                                                   END;
                                                END IF;

                                                --update technical contact details added on 30/12/2009
                                                SELECT COUNT (*)
                                                  INTO v_contactcnt
                                                  FROM site s,
                                                       site_contact_detail sc,
                                                       contact c
                                                 WHERE s.site_id = sc.site_id
                                                   AND sc.contact_id =
                                                                  c.contact_id
                                                   AND UPPER (c.contact_type) =
                                                          UPPER
                                                             (pkg_constants.con_tech_sitecontact
                                                             )
                                                   AND s.site_id = v_siteid
                                                   AND customer_id =
