                                                IF (v_contactcnt <> 0)
                                                THEN
                                                   BEGIN
                                                      SELECT sc.contact_id
                                                        INTO v_techcontactid
                                                        FROM site s,
                                                             site_contact_detail sc,
                                                             contact c
                                                       WHERE s.site_id =
                                                                    sc.site_id
                                                         AND sc.contact_id =
                                                                  c.contact_id
                                                         AND UPPER
                                                                (c.contact_type
                                                                ) =
                                                                UPPER
                                                                   (pkg_constants.con_tech_sitecontact
                                                                   )
                                                         AND s.site_id =
                                                                      v_siteid
                                                         AND customer_id =
                                                                 in_customerid;
