                                                                   v_countryid,
                                                       building_number =
                                                                 v_tbldgno (i),
                                                       address_line1 =
                                                              v_taddrline1 (i),
                                                       city = v_tcity (i),
                                                       state = v_tstate (i),
                                                       post_zip_code =
                                                               v_tpostcode (i),
                                                       room = v_troom (i),
                                                       FLOOR = v_tfloor (i),
                                                       address_line2 =
                                                              v_taddrline2 (i),
                                                       modified_by =
                                                                  in_createdby,
                                                       modified_date = SYSDATE
                                                 WHERE address_id =
                                                                   v_addressid;

                                                UPDATE site_contact_detail
                                                   SET modified_by =
                                                                  in_createdby,
                                                       modified_date = SYSDATE
                                                 WHERE site_id = v_siteid;

                                                -- update primary contact
                                                IF (v_pricontactid IS NULL)
                                                THEN
