                                                         )
                                                      THEN
                                                         BEGIN
                                                            INSERT INTO contact
                                                                        (contact_id,
                                                                         first_name,
                                                                         last_name,
                                                                         phone_number,
                                                                         mobile_number,
                                                                         fax,
                                                                         job_title,
                                                                         created_by,
                                                                         created_date,
                                                                         email,
                                                                         contact_type
                                                                        )
                                                                 VALUES (contact_id.NEXTVAL,
                                                                         v_tprifirstname
                                                                            (i),
                                                                         v_tprilastname
                                                                            (i),
                                                                         v_tpriphone
                                                                            (i),
                                                                         v_tprimobile
                                                                            (i),
                                                                         v_tprifax
                                                                            (i),
                                                                         v_tprijob
                                                                            (i),
                                                                         in_createdby,
                                                                         SYSDATE,
                                                                         v_tpriemail
                                                                            (i),
