   BEGIN
      OPEN c_sitedetails;

      --if the insert belongs the new batch id or same batch.
      IF (in_batchid IS NULL)
      THEN
         BEGIN
            INSERT INTO site_bulk_upload
                        (batch_id, file_name, created_by, created_date
                        )
                 VALUES (batch_id.NEXTVAL, in_filename, in_createdby, SYSDATE
                        )
              RETURNING batch_id
                   INTO v_batchid;
         END;
      ELSE
