                          FROM country
                         WHERE UPPER (REPLACE (NAME, '''')) =
                                        UPPER (REPLACE (v_tcountry (i), ''''));

                        IF (v_rowcount = 0
                           )           --country doesnt exist in country table
                        THEN
                           BEGIN
                              INSERT INTO site_log
                                          (site_log_id, batch_id,
                                           site_name,
                                           status,
                                           reason, created_by,
                                           created_date
                                          )
                                   VALUES (site_log_id.NEXTVAL, v_batchid,
                                           SUBSTR (v_currsite, 1, 100),
                                           pkg_constants.con_status_failure,
                                           'Invalid Country', in_createdby,
                                           SYSDATE
                                          );
                           END;
                        ELSE
                           BEGIN
                              SELECT country_id
                                INTO v_countryid
                                FROM country
                               WHERE UPPER (REPLACE (NAME, '''')) =
                                        UPPER (REPLACE (v_tcountry (i), ''''));

--                              SELECT COUNT (*)
--                                INTO v_rowcount
--                                FROM kgi_country
--                               WHERE country_id = v_countryid;

                              --                                --kgi check from PPSR
                              SELECT COUNT (*)
                                INTO v_rowcount
                                FROM ppsr_owner.ppsr_kgi_data_v@dblink_ppsr
                               WHERE UPPER (REPLACE ("Country", '''')) =
                                        UPPER (REPLACE (v_tcountry (i), ''''));

                              IF (v_rowcount != 0)
                              THEN
                                 BEGIN
                                    --------------KGI Check------------------
--                                    SELECT kgi_type
--                                      INTO v_kgitype
--                                      FROM kgi_country
--                                     WHERE country_id = v_countryid;

                                    --kgi check from PPSR
                                    SELECT DISTINCT "Kgi Type"
                                               INTO v_kgitype
                                               FROM ppsr_owner.ppsr_kgi_data_v@dblink_ppsr
                                              WHERE UPPER (REPLACE ("Country",
                                                                    ''''
                                                                   )
                                                          ) =
                                                       UPPER
                                                          (REPLACE
                                                               (v_tcountry (i),
                                                                ''''
                                                               )
                                                          );

                                    IF (UPPER (v_kgitype) LIKE '%CITY%')
                                    THEN
                                       BEGIN
                                          IF (v_tcity (i) IS NULL)
                                          THEN
                                             v_kgiflag := 0;
                                          END IF;
                                       END;
                                    END IF;

                                    IF (UPPER (v_kgitype) LIKE '%POST CODE%')
                                    THEN
                                       BEGIN
                                          IF (v_tpostcode (i) IS NULL)
                                          THEN
                                             v_kgiflag := 0;
                                          END IF;
                                       END;
                                    END IF;

                                    IF (   UPPER (v_kgitype) LIKE
                                                               '%STREET NAME%'
                                        OR UPPER (v_kgitype) LIKE
                                                             '%STREET NUMBER%'
                                       )
                                    THEN
                                       BEGIN
                                          IF (v_taddrline1 (i) IS NULL)
                                          THEN
                                             v_kgiflag := 0;
                                          END IF;
                                       END;
                                    END IF;

                                    IF (UPPER (v_kgitype) LIKE
                                                       '%TELEPHONE AREA CODE%'
                                       )
                                    THEN
                                       BEGIN
                                          IF (v_ttelareacode (i) IS NULL)
                                          THEN
                                             v_kgiflag := 0;
                                          END IF;
                                       END;
                                    END IF;

                                    IF (UPPER (v_kgitype) LIKE '%DISTRICT%')
                                    THEN
                                       BEGIN
                                          IF (v_taddrline2 (i) IS NULL)
                                          THEN
                                             v_kgiflag := 0;
                                          END IF;
                                       END;
                                    END IF;
                                 END;
                              END IF;

------------------------------------------------
                              IF (v_kgiflag = 1)
                              THEN
                                 success := 1;

                                 BEGIN
                                    SELECT COUNT (*)
                                      INTO v_rowcount
                                      FROM site
                                     WHERE UPPER (NAME) =
                                                       UPPER (v_tsitename (i))
                                       AND customer_id = in_customerid;

                                    IF (v_rowcount = 0)   --site doesnot exist
                                    THEN
                                       BEGIN
                                          INSERT INTO address
                                                      (address_id,
                                                       country_id,
                                                       building_number,
                                                       address_line1,
                                                       city,
                                                       state,
                                                       post_zip_code,
                                                       room,
                                                       FLOOR,
                                                       address_line2,
                                                       created_by,
                                                       created_date
                                                      )
                                               VALUES (address_id.NEXTVAL,
                                                       v_countryid,
                                                       v_tbldgno (i),
                                                       v_taddrline1 (i),
                                                       v_tcity (i),
                                                       v_tstate (i),
                                                       v_tpostcode (i),
                                                       v_troom (i),
                                                       v_tfloor (i),
                                                       v_taddrline2 (i),
                                                       in_createdby,
                                                       SYSDATE ()
                                                      )
                                            RETURNING address_id
                                                 INTO v_addressid;

                                          INSERT INTO site
                                                      (site_id,
                                                       NAME,
                                                       address_id,
                                                       customer_id,
                                                       telephone_area_code,
                                                       created_by,
                                                       created_date,
                                                       customer_report_site_name,
                                                       batch_id
                                                      )
                                               VALUES (site_id.NEXTVAL,
                                                       v_tsitename (i),
                                                       v_addressid,
                                                       in_customerid,
                                                       --  v_pricontactid,
                                                        -- v_seccontactid,
                                                       v_ttelareacode (i),
                                                       in_createdby,
                                                       SYSDATE,
                                                       v_tsitename (i),
                                                       v_batchid
                                                      )
                                            RETURNING site_id
                                                 INTO v_siteid;

                                          --insert primary contact details added on 30/12/2009
                                          IF (   v_tprifirstname (i) IS NOT NULL
                                              OR v_tprilastname (i) IS NOT NULL
                                              OR v_tpriemail (i) IS NOT NULL
                                              OR v_tpriphone (i) IS NOT NULL
                                              OR v_tprifax (i) IS NOT NULL
                                             )
                                          THEN
                                             BEGIN
                                                INSERT INTO contact
                                                            (contact_id,
                                                             first_name,
                                                             last_name,
                                                             phone_number,
                                                             mobile_number,
                                                             fax,
                                                             job_title,
                                                             contact_type,
                                                             created_by,
                                                             created_date,
                                                             email
                                                            )
                                                     VALUES (contact_id.NEXTVAL,
                                                             v_tprifirstname
                                                                           (i),
                                                             v_tprilastname
                                                                           (i),
                                                             v_tpriphone (i),
                                                             v_tprimobile (i),
                                                             v_tprifax (i),
                                                             v_tprijob (i),
                                                             pkg_constants.con_primary_sitecontact,
                                                             in_createdby,
                                                             SYSDATE (),
                                                             v_tpriemail (i)
                                                            )
                                                  RETURNING contact_id
                                                       INTO v_pricontactid;

                                                INSERT INTO site_contact_detail
                                                            (site_contact_detail_id,
                                                             site_id,
                                                             contact_id,
                                                             created_by,
                                                             created_date
                                                            )
                                                     VALUES (site_contact_detail_id.NEXTVAL,
                                                             v_siteid,
                                                             v_pricontactid,
                                                             in_createdby,
                                                             SYSDATE
                                                            );
                                             END;
                                          ELSE
                                             BEGIN
                                                v_pricontactid := NULL;
                                             END;
                                          END IF;

                                          --insert secondary contact details added on 30/12/2009
                                          IF (   v_tsecfirstname (i) IS NOT NULL
                                              OR v_tseclastname (i) IS NOT NULL
                                              OR v_tsecemail (i) IS NOT NULL
                                              OR v_tsecphone (i) IS NOT NULL
                                              OR v_tsecfax (i) IS NOT NULL
                                             )
                                          THEN
                                             BEGIN
                                                INSERT INTO contact
                                                            (contact_id,
                                                             first_name,
                                                             last_name,
                                                             phone_number,
                                                             mobile_number,
                                                             fax,
                                                             job_title,
                                                             contact_type,
                                                             created_by,
                                                             created_date,
                                                             email
                                                            )
                                                     VALUES (contact_id.NEXTVAL,
                                                             v_tsecfirstname
                                                                           (i),
                                                             v_tseclastname
                                                                           (i),
                                                             v_tsecphone (i),
                                                             v_tsecmobile (i),
                                                             v_tsecfax (i),
                                                             v_tsecjob (i),
                                                             pkg_constants.con_secondary_sitecontact,
                                                             in_createdby,
                                                             SYSDATE (),
                                                             v_tsecemail (i)
                                                            )
                                                  RETURNING contact_id
                                                       INTO v_seccontactid;

                                                INSERT INTO site_contact_detail
                                                            (site_contact_detail_id,
                                                             site_id,
                                                             contact_id,
                                                             created_by,
                                                             created_date
                                                            )
                                                     VALUES (site_contact_detail_id.NEXTVAL,
                                                             v_siteid,
                                                             v_seccontactid,
                                                             in_createdby,
                                                             SYSDATE
                                                            );
                                             END;
                                          ELSE
                                             --added on 22/12/2009 to handle null secondary contact details
                                             BEGIN
                                                v_seccontactid := NULL;
                                             END;
                                          END IF;

                                          --insert technical contact details added on 30/12/2009
                                          IF (   v_ttechfname (i) IS NOT NULL
                                              OR v_ttechlname (i) IS NOT NULL
                                              OR v_ttechemail (i) IS NOT NULL
                                              OR v_ttechphone (i) IS NOT NULL
                                              OR v_ttechfax (i) IS NOT NULL
                                             )
                                          THEN
                                             BEGIN
                                                INSERT INTO contact
                                                            (contact_id,
                                                             first_name,
                                                             last_name,
                                                             phone_number,
                                                             mobile_number,
                                                             fax,
                                                             job_title,
                                                             contact_type,
                                                             created_by,
                                                             created_date,
                                                             email
                                                            )
                                                     VALUES (contact_id.NEXTVAL,
                                                             v_ttechfname (i),
                                                             v_ttechlname (i),
                                                             v_ttechphone (i),
                                                             v_ttechmobile (i),
                                                             v_ttechfax (i),
                                                             v_ttechjob (i),
                                                             pkg_constants.con_tech_sitecontact,
                                                             in_createdby,
                                                             SYSDATE (),
                                                             v_ttechemail (i)
                                                            )
                                                  RETURNING contact_id
                                                       INTO v_techcontactid;

                                                INSERT INTO site_contact_detail
                                                            (site_contact_detail_id,
                                                             site_id,
                                                             contact_id,
                                                             created_by,
                                                             created_date
                                                            )
                                                     VALUES (site_contact_detail_id.NEXTVAL,
                                                             v_siteid,
                                                             v_techcontactid,
                                                             in_createdby,
                                                             SYSDATE
                                                            );
                                             END;
                                          ELSE
                                             --added on 22/12/2009 to handle null secondary contact details
                                             BEGIN
                                                v_techcontactid := NULL;
                                             END;
                                          END IF;
                                       END;
                                    ELSE          --update if row is duplicate
                                       BEGIN
                                          v_contactcnt := NULL;

                                          SELECT site_id, address_id
                                            INTO v_siteid, v_addressid
                                            FROM site
                                           WHERE UPPER (NAME) =
                                                       UPPER (v_tsitename (i))
                                             AND customer_id = in_customerid;

                                          --Modified on 23-Jan-2010, do not allow any modification of a site
                                          --if it is associated to a quote.
                                          SELECT COUNT (*)
                                            INTO v_rowcount
                                            FROM quote_site
                                           WHERE site_id = v_siteid
                                             AND active =
                                                    pkg_constants.con_activeflag_yes;

                                          IF (v_rowcount = 0)
                                          THEN
                                             BEGIN
                                                --update primary contact details added on 30/12/2009
                                                SELECT COUNT (*)
                                                  INTO v_contactcnt
                                                  FROM site s,
                                                       site_contact_detail sc,
                                                       contact c
                                                 WHERE s.site_id = sc.site_id
                                                   AND sc.contact_id =
                                                                  c.contact_id
                                                   AND UPPER (c.contact_type) =
                                                          UPPER
                                                             (pkg_constants.con_primary_sitecontact
                                                             )
                                                   AND s.site_id = v_siteid
                                                   AND customer_id =
                                                                 in_customerid;

                                                IF (v_contactcnt <> 0)
                                                THEN
                                                   BEGIN
                                                      SELECT sc.contact_id
                                                        INTO v_pricontactid
                                                        FROM site s,
                                                             site_contact_detail sc,
                                                             contact c
                                                       WHERE s.site_id =
                                                                    sc.site_id
                                                         AND sc.contact_id =
                                                                  c.contact_id
                                                         AND UPPER
                                                                (c.contact_type
                                                                ) =
                                                                UPPER
                                                                   (pkg_constants.con_primary_sitecontact
                                                                   )
                                                         AND s.site_id =
                                                                      v_siteid
                                                         AND customer_id =
                                                                 in_customerid;
                                                   END;
                                                ELSE
                                                   BEGIN
                                                      v_pricontactid := NULL;
                                                   END;
                                                END IF;

                                                --update secondary contact details added on 30/12/2009
                                                SELECT COUNT (*)
                                                  INTO v_contactcnt
                                                  FROM site s,
                                                       site_contact_detail sc,
                                                       contact c
                                                 WHERE s.site_id = sc.site_id
                                                   AND sc.contact_id =
                                                                  c.contact_id
                                                   AND UPPER (c.contact_type) =
                                                          UPPER
                                                             (pkg_constants.con_secondary_sitecontact
                                                             )
                                                   AND s.site_id = v_siteid
                                                   AND customer_id =
                                                                 in_customerid;

                                                IF (v_contactcnt <> 0)
                                                THEN
                                                   BEGIN
                                                      SELECT sc.contact_id
                                                        INTO v_seccontactid
                                                        FROM site s,
                                                             site_contact_detail sc,
                                                             contact c
                                                       WHERE s.site_id =
                                                                    sc.site_id
                                                         AND sc.contact_id =
                                                                  c.contact_id
                                                         AND UPPER
                                                                (c.contact_type
                                                                ) =
                                                                UPPER
                                                                   (pkg_constants.con_secondary_sitecontact
                                                                   )
                                                         AND s.site_id =
                                                                      v_siteid
                                                         AND customer_id =
                                                                 in_customerid;
                                                   END;
                                                ELSE
                                                   BEGIN
                                                      v_seccontactid := NULL;
                                                   END;
                                                END IF;

                                                --update technical contact details added on 30/12/2009
                                                SELECT COUNT (*)
                                                  INTO v_contactcnt
                                                  FROM site s,
                                                       site_contact_detail sc,
                                                       contact c
                                                 WHERE s.site_id = sc.site_id
                                                   AND sc.contact_id =
                                                                  c.contact_id
                                                   AND UPPER (c.contact_type) =
                                                          UPPER
                                                             (pkg_constants.con_tech_sitecontact
                                                             )
                                                   AND s.site_id = v_siteid
                                                   AND customer_id =
                                                                 in_customerid;

                                                IF (v_contactcnt <> 0)
                                                THEN
                                                   BEGIN
                                                      SELECT sc.contact_id
                                                        INTO v_techcontactid
                                                        FROM site s,
                                                             site_contact_detail sc,
                                                             contact c
                                                       WHERE s.site_id =
                                                                    sc.site_id
                                                         AND sc.contact_id =
                                                                  c.contact_id
                                                         AND UPPER
                                                                (c.contact_type
                                                                ) =
                                                                UPPER
                                                                   (pkg_constants.con_tech_sitecontact
                                                                   )
                                                         AND s.site_id =
                                                                      v_siteid
                                                         AND customer_id =
                                                                 in_customerid;
                                                   END;
                                                ELSE
                                                   BEGIN
                                                      v_techcontactid := NULL;
                                                   END;
                                                END IF;

                                                UPDATE site
                                                   SET modified_by =
                                                                  in_createdby,
                                                       modified_date = SYSDATE,
                                                       customer_report_site_name =
                                                               v_tsitename (i),
                                                       telephone_area_code =
                                                            v_ttelareacode (i)
                                                 WHERE site_id = v_siteid;

                                                UPDATE address
                                                   SET country_id =
                                                                   v_countryid,
                                                       building_number =
                                                                 v_tbldgno (i),
                                                       address_line1 =
                                                              v_taddrline1 (i),
                                                       city = v_tcity (i),
                                                       state = v_tstate (i),
                                                       post_zip_code =
                                                               v_tpostcode (i),
                                                       room = v_troom (i),
                                                       FLOOR = v_tfloor (i),
                                                       address_line2 =
                                                              v_taddrline2 (i),
                                                       modified_by =
                                                                  in_createdby,
                                                       modified_date = SYSDATE
                                                 WHERE address_id =
                                                                   v_addressid;

                                                UPDATE site_contact_detail
                                                   SET modified_by =
                                                                  in_createdby,
                                                       modified_date = SYSDATE
                                                 WHERE site_id = v_siteid;

                                                -- update primary contact
                                                IF (v_pricontactid IS NULL)
                                                THEN
                                                   BEGIN
                                                      IF (   v_tprifirstname
                                                                           (i) IS NOT NULL
                                                          OR v_tprilastname
                                                                           (i) IS NOT NULL
                                                          OR v_tpriemail (i) IS NOT NULL
                                                          OR v_tpriphone (i) IS NOT NULL
                                                          OR v_tprifax (i) IS NOT NULL
                                                         )
                                                      THEN
                                                         BEGIN
                                                            INSERT INTO contact
                                                                        (contact_id,
                                                                         first_name,
                                                                         last_name,
                                                                         phone_number,
                                                                         mobile_number,
                                                                         fax,
                                                                         job_title,
                                                                         created_by,
                                                                         created_date,
                                                                         email,
                                                                         contact_type
                                                                        )
                                                                 VALUES (contact_id.NEXTVAL,
                                                                         v_tprifirstname
                                                                            (i),
                                                                         v_tprilastname
                                                                            (i),
                                                                         v_tpriphone
                                                                            (i),
                                                                         v_tprimobile
                                                                            (i),
                                                                         v_tprifax
                                                                            (i),
                                                                         v_tprijob
                                                                            (i),
                                                                         in_createdby,
                                                                         SYSDATE,
                                                                         v_tpriemail
                                                                            (i),
                                                                         pkg_constants.con_primary_sitecontact
                                                                        )
                                                              RETURNING contact_id
                                                                   INTO v_pricontactid;

                                                            INSERT INTO site_contact_detail
                                                                        (site_contact_detail_id,
                                                                         site_id,
                                                                         contact_id,
                                                                         created_by,
                                                                         created_date
                                                                        )
                                                                 VALUES (site_contact_detail_id.NEXTVAL,
                                                                         v_siteid,
                                                                         v_pricontactid,
                                                                         in_createdby,
                                                                         SYSDATE
                                                                        );
                                                         END;
                                                      END IF;
                                                   END;
                                                ELSE
                                                   BEGIN
                                                      UPDATE contact
                                                         SET first_name =
                                                                v_tprifirstname
                                                                           (i),
                                                             last_name =
                                                                v_tprilastname
                                                                           (i),
                                                             phone_number =
                                                                v_tpriphone
                                                                           (i),
                                                             mobile_number =
                                                                v_tprimobile
                                                                           (i),
