                                                                 v_tprifax (i),
                                                             job_title =
                                                                 v_tprijob (i),
                                                             contact_type =
                                                                pkg_constants.con_primary_sitecontact,
                                                             email =
                                                                v_tpriemail
                                                                           (i),
                                                             modified_by =
                                                                  in_createdby,
                                                             modified_date =
                                                                       SYSDATE
                                                       WHERE contact_id =
                                                                v_pricontactid;
                                                   END;
                                                END IF;

                                                -- update secondary contact
                                                IF (v_seccontactid IS NULL)
                                                THEN
                                                   BEGIN
                                                      IF (   v_tsecfirstname
                                                                           (i) IS NOT NULL
                                                          OR v_tseclastname
                                                                           (i) IS NOT NULL
                                                          OR v_tsecemail (i) IS NOT NULL
                                                          OR v_tsecphone (i) IS NOT NULL
                                                          OR v_tsecfax (i) IS NOT NULL
                                                         )
                                                      THEN
                                                         BEGIN
                                                            INSERT INTO contact
                                                                        (contact_id,
                                                                         first_name,
                                                                         last_name,
