                                                                         fax,
                                                                         job_title,
                                                                         created_by,
                                                                         created_date,
                                                                         email,
                                                                         contact_type
                                                                        )
                                                                 VALUES (contact_id.NEXTVAL,
                                                                         v_tsecfirstname
                                                                            (i),
                                                                         v_tseclastname
                                                                            (i),
                                                                         v_tsecphone
                                                                            (i),
                                                                         v_tsecmobile
                                                                            (i),
                                                                         v_tsecfax
                                                                            (i),
                                                                         v_tsecjob
                                                                            (i),
                                                                         in_createdby,
                                                                         SYSDATE,
                                                                         v_tsecemail
                                                                            (i),
                                                                         pkg_constants.con_secondary_sitecontact
                                                                        )
                                                              RETURNING contact_id
                                                                   INTO v_seccontactid;

                                                            INSERT INTO site_contact_detail
                                                                        (site_contact_detail_id,
                                                                         site_id,
                                                                         contact_id,
                                                                         created_by,
                                                                         created_date
                                                                        )
                                                                 VALUES (site_contact_detail_id.NEXTVAL,
                                                                         v_siteid,
                                                                         v_seccontactid,
                                                                         in_createdby,
                                                                         SYSDATE
                                                                        );
                                                         END;
                                                      END IF;
                                                   END;
                                                ELSE
                                                   BEGIN
                                                      UPDATE contact
                                                         SET first_name =
                                                                v_tsecfirstname
                                                                           (i),
                                                             last_name =
                                                                v_tseclastname
                                                                           (i),
                                                             phone_number =
                                                                v_tsecphone
                                                                           (i),
                                                             mobile_number =
                                                                v_tsecmobile
                                                                           (i),
                                                             fax =
                                                                 v_tsecfax (i),
                                                             job_title =
                                                                 v_tsecjob (i),
                                                             contact_type =
                                                                pkg_constants.con_secondary_sitecontact,
                                                             email =
                                                                v_tsecemail
                                                                           (i),
                                                             modified_by =
                                                                  in_createdby,
                                                             modified_date =
                                                                       SYSDATE
                                                       WHERE contact_id =
                                                                v_seccontactid;
                                                   END;
                                                END IF;

                                                IF (v_techcontactid IS NULL)
                                                THEN
                                                   BEGIN
                                                      IF (   v_ttechfname (i) IS NOT NULL
                                                          OR v_ttechlname (i) IS NOT NULL
                                                          OR v_ttechemail (i) IS NOT NULL
                                                          OR v_ttechphone (i) IS NOT NULL
                                                          OR v_ttechfax (i) IS NOT NULL
                                                         )
                                                      THEN
                                                         BEGIN
                                                            INSERT INTO contact
                                                                        (contact_id,
                                                                         first_name,
                                                                         last_name,
                                                                         phone_number,
                                                                         mobile_number,
                                                                         fax,
                                                                         job_title,
                                                                         created_by,
                                                                         created_date,
                                                                         email,
                                                                         contact_type
                                                                        )
                                                                 VALUES (contact_id.NEXTVAL,
                                                                         v_ttechfname
                                                                            (i),
                                                                         v_ttechlname
                                                                            (i),
                                                                         v_ttechphone
                                                                            (i),
                                                                         v_ttechmobile
                                                                            (i),
                                                                         v_ttechfax
                                                                            (i),
                                                                         v_ttechjob
                                                                            (i),
                                                                         in_createdby,
                                                                         SYSDATE,
                                                                         v_ttechemail
                                                                            (i),
                                                                         pkg_constants.con_tech_sitecontact
                                                                        )
                                                              RETURNING contact_id
                                                                   INTO v_techcontactid;

                                                            INSERT INTO site_contact_detail
                                                                        (site_contact_detail_id,
                                                                         site_id,
                                                                         contact_id,
                                                                         created_by,
                                                                         created_date
                                                                        )
                                                                 VALUES (site_contact_detail_id.NEXTVAL,
                                                                         v_siteid,
                                                                         v_techcontactid,
                                                                         in_createdby,
                                                                         SYSDATE
                                                                        );
                                                         END;
