                            AND l.order_header_id     = v_order_header_id
                            AND l.site_id             = v_site_id
                            AND UPPER (l.action_code) <> 'ADD'
                            AND UPPER(l.fallback_type) = in_fallbck_tobe)
                  AND EXISTS (
                         SELECT 1
                           FROM order_line_item l
