      --  DSL COMMERCIAL PORT SPEED UPDATE
        BEGIN
           SELECT 1
           INTO v_access_tech4
           FROM line_item_detail
           WHERE active = 'Y'
            AND line_item_id IN (SELECT l.line_item_id
                                 FROM line_item l
                                WHERE l.active               = 'Y'
                                  AND l.quote_header_id      = in_quote_header_id
