        WHEN UPPER(sqe_PRICE_tbl_TE(i).price_line_description) = 'ADDITIONAL VPN CONNECTION - PRIMARY - MTHLY' THEN
          V_Prim_mVPN_Mon_Rntal_PTP_Port                      := sqe_PRICE_tbl_TE(i).price;
