        WHEN UPPER(sqe_PRICE_tbl_TE(i).price_line_description) = 'MVPN RENTAL PTP - TOTAL PER ADDITION VPN - PRIMARY - MTHLY-RECURRING' THEN
          V_Pr_mVPN_MnRnt_PTP_taddVPN                         := sqe_PRICE_tbl_TE(i).price;
