        WHEN UPPER(sqe_PRICE_tbl_TE(i).price_line_description) = 'BGP CONFIGURATION CHARGE - PRIMARY - INSTALL' THEN
          V_Pr_BGP_Prefx_One_Time_PTP                         := sqe_PRICE_tbl_TE(i).price;
