            end if;
        WHEN UPPER(sqe_PRICE_tbl_TE(i).price_line_description) = 'SNMP READ ONLY CHARGE' THEN
          V_SNMP_RdAcs_Pr_CPE_OnTm_PTP                        := sqe_PRICE_tbl_TE(i).price;
           IF INSTR(V_SNMP_RdAcs_Pr_CPE_OnTm_PTP,'.') = 0 THEN
