            end if;
        WHEN UPPER(sqe_PRICE_tbl_TE(i).price_line_description) = 'OUT OF OFFICE INSTALLATION CHARGE' THEN
          V_OOH_trn_up_Mn_CPE_OneTm_PTP                       := sqe_PRICE_tbl_TE(i).price;
