               IF (v_costtype IS NOT NULL)
               THEN
                  INSERT INTO cost_detail
                              (cost_detail_id, price_line_item_detail_id,
                               cost_type, COST, currency, created_by,
                               created_date)
                     (SELECT cost_detail_id.NEXTVAL,
