                                            AND (   site_id = in_siteid
                                                 OR (    site_id IS NULL
                                                     AND NVL (in_siteid, 0) =
                                                                             0
                                                    )
                                                )
                                            /*904 Order Site to Site SLA */
                                            AND NVL (part_type, '-1') =
                                                   DECODE
                                                         (UPPER (product_name),
                                                          'BT CPE', 'Bundle',
                                                          NVL (part_type,
                                                               '-1')
                                                         )
                                            --for BT Cpe only part type called Bundle has to be picked up
                                            AND UPPER (product_name) =
