                                                )
                                  --for BT Cpe only part type called Bundle has to be picked up
                                  AND UPPER (product_name) =
                                                          UPPER (v_priclinitm)
                                  ----in_prodname) GHUCS5
                                  AND active =
                                              pkg_constants.con_activeflag_yes
                                  AND UPPER (fallback_type) =
