                  AND (   site_id = in_siteid
                       OR (site_id IS NULL AND NVL (in_siteid, 0) = 0)
                      )                        /*904 Order Site to Site SLA */
                  AND UPPER (product_name) = UPPER (v_priclinitm)
                  ----in_prodname) GHUCS 4
                  AND NVL (part_type, '-1') =
