                      non_std_qref_flag,                       --added by Mahim
                      /*Start Added by Suresh Panati for Bulk Upload*/
                      ape_comments,
                      apop_code,
                      cease_charges,
                      gpop,
                      gpop_code,
                      network_type,
                      partner_name,
                      ptp_mrc,
                      ptp_nrc,
                      qref_type,
                      rbpurl,
                      resiliency,
                      staff_member_email,
                      staff_member_name,
                      support_24_7,
                      workflow_status,
                      service_variant
              --            ,
--            /*End Added by Suresh Panati for Bulk Upload*/
--            /*Start Added by Nagendra for Bulk Upload on 31-May-2011*/
--                      optm_qref,
--                      optm_rental_cost_y1,
--                      optm_rental_cost_y2,
--                      optm_rental_cost_y3,
--                      optm_rental_cost_y4,
--                      optm_rental_cost_y5,
--                      optm_rental_price_y1,
--                      optm_rental_price_y2,
