                             NULL,                           -- o_discount
                             NULL,                           -- o_disctype
                             v_fallback_type,                -- o_fallback
                             v_monthly_cost,                 -- o_cost
                             'Monthly Cost',                 -- o_costtype
                             v_currency,                     -- o_costcur
                             v_cheap_aqref,                  -- o_extqref
                             NULL,                           -- o_reqstatus
                             NULL,                           -- o_errdesc
                             NULL,                           -- o_pripricingstatus
                             NULL,                           -- o_secpricingstatus
