         SUBSTR (v_row,
                 INSTR (v_row, '|', 1, 1) + 1,
                 LENGTH (v_row) - INSTR (v_row, '|', 1, 1)
                );
      v_ein := TO_NUMBER (SUBSTR (v_row, 1, INSTR (v_row, '|', 1, 1) - 1));
