                       AND UPPER (x.VALUE) LIKE
                              CASE
                                 WHEN (NVL (y.non_std_flag, 'X') =
                                                     NVL (x.non_std_flag, 'X')
                                      )
                                    THEN UPPER (y.VALUE) || '%'
                                 ELSE UPPER (x.VALUE) || '%'
                              END
                         ;

--Added v_sitecnt condition by Tanmayi on 20/09/2010
                  IF (v_dslcnt = 0 AND v_sitecnt = 0)
                  THEN
