-- v_cnt has count of sites with resiliency standard-unbundled.
                     SELECT COUNT (*)
                       INTO v_cnt
                       FROM line_item li INNER JOIN line_item_detail lid
                            ON li.line_item_id = lid.line_item_id
                          AND li.active = pkg_constants.con_activeflag_yes
