                                                NULL,
                                                NULL,
                                                'PKG_ORDER',
                                                'SPS_VALIDATE_ORDER',
                                                out_status,
                                                'pricing status is budgetary'
                                               );
                        END IF;
                     END;
                  ELSE
--if connector types dont match
                     IF (v_port_only_hardwire = 0)
                     THEN
                        out_status := pkg_constants.con_status_failure;
                        pkg_common.spi_error_log (NULL,
