                         ON x.quote_header_id = y.quote_header_id
                       AND x.site_id = y.site_id
--AND upper(x.VALUE) LIKE upper(y.VALUE) || '%'
-- Added on 12/4/2011 as part of defect fix for 104683
                       AND UPPER (x.VALUE) LIKE
