                         in_quoteheaderid,
                         NULL,
                         NULL,
                         'PKG_ORDER',
                         'SPS_VALIDATE_ORDER',
                         out_status,
                         'where connector types also match and dsl is also there for the same site'
                        );
                  END IF;

               END IF;                           -- IF v_multisite_dsl_cnt = 0

            END IF;
         END;
      END IF;

